  const getProduct = async () => {
    try {
      let response = await fetch(URL);
      let detail = await response.json();
      setProduct(detail);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getDetails();
  });

  return
  <>
  <h1>{product.title}</h1>
			<div>
