import { useEffect, useState } from 'react';
import Product from './Product';

function Products({ selectedCategory }) {
  const [products, setProducts] = useState([]);

  async function getProducts() {
    const response = await fetch('https://fakestoreapi.com/products/');
    const productList = await response.json();
    setProducts(productList);
  }

  async function getFilteredProducts() {
    const response = await fetch(`https://fakestoreapi.com/products/category/${selectedCategory}`);
    const filteredProductList = await response.json();
    setProducts(filteredProductList);
  }

  useEffect(() => {
    selectedCategory !== '' ? getFilteredProducts() : getProducts();
  }, [selectedCategory]);

  return (
    <ul className="products">
      {products.map((product) => (
        <Product id={product.id} title={product.title} image={product.image} key={product.id} />
      ))}
    </ul>
  );
}

export default Products;
