import './App.css';
import categoryList from './fake-data/all-categories';
import productList from './fake-data/all-products';
import { useState } from 'react';

function Header() {
  return <h1 className="header">Products</h1>;
}

function Categories({filterProducts}) {
  return (
    <div className="categories">
      {categoryList.map((categoryName, index) => (
        <Category title={categoryName} key={index} />
      ))}
    </div>
  );
}

function Category({ title, filterProducts }) {
  return (
    <span className="categories--item" onClick={filterProducts}>
      {title}
    </span>
  );
}

function Products({displayedProducts}) {
  return (
    <ul className="products">
      {displayedProducts.map((product, index) => (
        <Product title={product.title} image={product.image} key={index} />
      ))}
    </ul>
  );
}

function Product({ title, image }) {
  return (
    <li className="products--item">
      <div className="product">
        <img className="product--image" src={image} alt="product_image" />
        <span className="product--title">{title}</span>
      </div>
    </li>
  );
}

function App() {
  const [allProducts, setProducts] = useState(productList);

  function filterProducts(e) {
    const selectedCategory = e.target.textContent;
    const filteredProducts = productList.filter(
      (product) => `FAKE: ${product.category}` === selectedCategory
    );

    setProducts(filteredProducts);
  }
  return (
    <div className="App">
      <Header />
      <Categories filterProducts={filterProducts}/>
      <Products displayedProducts={allProducts} />
    </div>
  );
}

export default App;
