import Category from './Category';
import { useState } from 'react';
import useFetch from '../hooks/useFetch';

function Categories({
  selectedCategory,
  setSelectedCategory,
  checkLoading: [isLoading, setIsLoading],
  checkError: [isError, setIsError],
}) {
  const [categories, setCategories] = useState([]);
  // const { data, isLoading, isError} = useFetch('https://fakestoreapi.com/products/categories')

  useEffect(() => {
    async function getCategories() {
      try {
        const response = await fetch('https://fakestoreapi.com/products/categories');
        const allCategories = await response.json();
        setCategories(allCategories);
      } catch (error) {
        console.log(error);
        setIsError(true);
      }
      setIsLoading(false);
    }
    getCategories();
  }, []);

  // console.log(data);
  // setCategories(data)


  function selectCategory(e) {
    const selectedCategoryName = e.target.textContent;
    setSelectedCategory(selectedCategoryName);
  }

  return (
    <div className="categories">
      {isError ? (
        <span>BAD REQUEST!</span>
      ) : isLoading ? (
        <span>Loading...</span>
      ) : (
        categories && categories.map((categoryName, index) => (
          <Category
            title={categoryName}
            selectCategory={selectCategory}
            selectedCategory={selectedCategory}
            key={index}
          />
        ))
      )}
    </div>
  );
}

export default Categories;
